import React, {Component} from 'react';
import {Table, Menu, Icon, Button, Input, Layout, Breadcrumb, Modal} from 'antd';
import {BrowserRouter as Router, Route, Link} from "react-router-dom";
import Highlighter from 'react-highlight-words';

import './App.css';
import reqwest from 'reqwest';

const {SubMenu} = Menu;
const {Header, Content, Sider} = Layout;

class ResultTable extends Component {
    constructor(props) {
        super(props);
    }

    state = {
        searchText: '',
    };

    getColumnSearchProps = dataIndex => ({
        filterDropdown: ({setSelectedKeys, selectedKeys, confirm, clearFilters}) => (
            <div style={{padding: 8}}>
                <Input
                    ref={node => {
                        this.searchInput = node;
                    }}
                    placeholder={`Search ${dataIndex}`}
                    value={selectedKeys[0]}
                    onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
                    onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
                    style={{width: 188, marginBottom: 8, display: 'block'}}
                />
                <Button
                    type="primary"
                    onClick={() => this.handleSearch(selectedKeys, confirm)}
                    icon="search"
                    size="small"
                    style={{width: 90, marginRight: 8}}
                >
                    Search
                </Button>
                <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{width: 90}}>
                    Reset
                </Button>
            </div>
        ),
        filterIcon: filtered => (
            <Icon type="search" style={{color: filtered ? '#1890ff' : undefined}}/>
        ),
        onFilter: (value, record) =>
            record[dataIndex]
                .toString()
                .toLowerCase()
                .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
            if (visible) {
                setTimeout(() => this.searchInput.select());
            }
        },
        render: text => (
            <Highlighter
                highlightStyle={{backgroundColor: '#ffc069', padding: 0}}
                searchWords={[this.state.searchText]}
                autoEscape
                textToHighlight={text.toString()}
            />
        ),
    });

    handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({searchText: selectedKeys[0]});
    };

    handleReset = clearFilters => {
        clearFilters();
        this.setState({searchText: ''});
    };

    render() {
        const columns = [
            {
                title: 'Code',
                dataIndex: 'stock_id',
                key: 'stock_id',
                width: '20%',
                ...this.getColumnSearchProps('stock_id'),
            },
            {
                title: 'Name',
                dataIndex: 'stock_name',
                key: 'stock_name',
                width: '20%',
                ...this.getColumnSearchProps('stock_name'),
            },
            {
                title: 'Price',
                dataIndex: 'stock_price',
                key: 'stock_price',
                width: '15%',
            },
            {
                title: 'High',
                dataIndex: 'upper_limit',
                key: 'upper_limit',
                width: '15%',
            },
            {
                title: 'Low',
                dataIndex: 'lower_limit',
                key: 'lower_limit',
                width: '15%',
            },
            {
                title: 'State',
                dataIndex: 'stock_state',
                key: 'stock_state',
                width: '15%',
                ...this.getColumnSearchProps('stock_state'),
            },
        ];
        return <Table columns={columns} dataSource={this.props.data}/>;
    }
}

