            },
            {
                title: 'Low',
                dataIndex: 'lower_limit',
                render: low => <span>{low}</span>,
            },
            {
                title: 'State',
                dataIndex: 'stock_state',
                render: state => <span>{state}</span>,
            },

            /* the following column contains colored badgers
            * could be used to color the ups (red) and downs (green) */
            // {
